INSERT INTO `databaseweb`.`versione_prodotto` (`Di_Codice_prodotto`, `Codice_prodotto`, `Marchio`, `Descrizione`, `Prezzo`, `Disponibilita`, `Nome_immagine`) VALUES ('000122', '_0_0', 'Scoutmarchio', 'Il distintivo di Progressione Personale per esploratori e guide per la prima tappa, la tappa della Scoperta: la tenda di Squadriglia per indicare il primo elemento significativo della vita di Sq. che ogni E/G incontra all’inizio del Sentiero e caratterizzante dell’avventura dello scouting.', '2.00', '16', 'Distintivo-di-progressione-personale-prima-tappa.png');
