INSERT INTO `databaseweb`.`versione_prodotto` (`Di_Codice_prodotto`, `Codice_prodotto`, `Marchio`, `Descrizione`, `Prezzo`, `Disponibilita`, `Nome_immagine`) VALUES ('000124', '_0_0', 'Scoutmarchio', 'Distintivo di Progressione Personale per il cerchio. E’ il distintivo che riguarda la seconda fase del gioco dei voli. Coccinella del bosco è la fase del volo un po’ più impegnativo, in cui il bambino è concentrato su di sé e sul far sì che la gioia scoperta non sia passeggera ed effimera, ma diventi parte del suo modo di essere.', '1.70', '38', 'dist-prog-pers-cerchio-coccinella-del-bosco.png');
